def count_nb_handles(handles)
    nb_handles = handles.length
    puts "Il y a #{nb_handles} handles dans cette array."
end

def find_shortest_handle(handles)
    shortest_handle = handles.min {|a,b| a.size <=> b.size}
    puts "Le handle le plus court est #{shortest_handle}."
end

def count_handles_5_caracters(handles)
    nb_handles_5_caracters = handles.count {|a| a.length == 6}
    puts "Il y a #{nb_handles_5_caracters} handles avec 5 caractères."
end

def count_handles_uppercase(handles)
    nb_handles_uppercase = handles.count {|i| i[1].ord.between?(("A".ord),("Z".ord))}
    puts "#{nb_handles_uppercase} handles commencent par une majuscule."
end

def count_handles_uppercase_bis(handles)
    nb_handles_uppercase_bis = handles.count {|x| x[1].match?( /[[:upper:]]/)}
    puts "#{nb_handles_uppercase_bis} handles commencent par une majuscule."
end

def rank_alphabetical_order(handles)
    handles_ranked_alphabetical = handles.sort_by {|x| x.downcase}
    puts "Voici les handles dans l'ordre alphabétique :"
    puts handles_ranked_alphabetical
end

def rank_length_order(handles)
    handles_ranked_length = handles.sort_by {|x| x.length}
    puts "Voici les handles classées par taille :"
    puts handles_ranked_length
end

def find_position(handles)
    position = handles.index("@epenser")
    puts "@epenser est en #{position}ème position."
end

def repartition(handles)
    nb_characters = 1
    while nb_characters <= 33
        puts "#{handles.count {|x| x.length == nb_characters}} handles ont une longueur de #{nb_characters} caractère(s)."
        nb_characters += 1
    end
end

def menu(handles)
    puts "Entrez le chiffre correspondant à la requête pour voir la réponse :
    1 - Nombre de handles
    2 - Handle le plus court
    3 - Nombre de handles contenant 5 caractères
    4 - Nombre de handles commençant par une majuscule
    5 - Liste triée par ordre alphabétique
    6 - Liste triée par taille des handles
    7 - Position dans l'array de @epenser
    8 - Répartition des handles par taille
    9 - Exit"
    print "> "
    number = gets.to_i 
    if number == 1
        puts count_nb_handles(handles)
    elsif number == 2
        find_shortest_handle(handles)
    elsif number == 3
        count_handles_5_caracters(handles)
    elsif number == 4
        count_handles_uppercase(handles)
    elsif number == 5
        rank_alphabetical_order(handles)
    elsif number == 6
        rank_length_order(handles)
    elsif number == 7
        find_position(handles)
    elsif number == 8
        repartition(handles)
    elsif number == 9
        exit
    else
        puts "Veuillez entrer un numéro valide."
    end
    puts "\n"
    menu(handles)
end

menu(handles)